mapper.select:
SELECT p.ID as ID, p.NAME as NAME, p.PRICE as PRICE, c1.CODE_NAME as BRAND, c2.CODE_NAME as CATEGORY, p.IMAGEURL as IMAGEURL FROM PRODUCT p JOIN common c1 ON p.BRAND = c1.CODE_ID JOIN common c2 ON p.CATEGORY = c2.CODE_ID
AND p.BRAND = #{brand}
ANd p.CATEGORY = #{category}

mapper.insert:

    SELECT
        SEQ_PRODUCT.NEXTVAL
    FROM
        DUAL

INSERT INTO PRODUCT
    ()
VALUES
    (
        #{id}
        ,#{name}
        ,#{price}
        ,#{brand}

            ,#{category}


            ,NULL


            ,#{imageUrl}


            ,NULL

    )
